import React, { useState } from "react";
import {FaPlus, FaCopy, FaEye, FaEyeSlash, FaPencilAlt, FaTrash,} from "react-icons/fa";
import logo from "../images/gandalflogo.jpg";

// Initial Data
const initialPasswords = [
  {
    site: "Bestbuy",
    username: "Testingaccount",
    password: "password123",
    website: "bestbuy.com",
  },
  {
    site: "Amazon",
    username: "Testingaccount",
    password: "amazonpass",
    website: "amazon.com",
  },
  {
    site: "Netflix",
    username: "Testingaccount",
    password: "netflixpass",
    website: "netflix.com",
  },
];

// Main Component
export default function PasswordManager() {
  const [passwords, setPasswords] = useState(initialPasswords);
  const [selected, setSelected] = useState(initialPasswords[0]);
  const [isEditing, setIsEditing] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // Helper to update the selected entry's fields
  const handleFieldChange = (field, value) => {
    setSelected({ ...selected, [field]: value });
  };

  // Save changes to the list
  const handleSave = () => {
    setPasswords((prev) =>
      prev.map((p) => (p === selected ? selected : p))
    );
    setIsEditing(false);
  };

  const handleCreateNew = () => {
    const blank = { site: "", username: "", password: "", website: "" };
    setPasswords((prev) => [...prev, blank]);
    setSelected(blank);
    setIsEditing(true);
    setShowPassword(false);
  };

  const handleDelete = () => {
    if (!selected) return;
    setPasswords((prev) => prev.filter((entry) => entry !== selected));
    setSelected(null);
    setIsEditing(false);
    setShowPassword(false);
  };

  // When a vault item is selected from the sidebar
  const handleSelect = (entry) => {
    setSelected(entry);
    setIsEditing(false);
    setShowPassword(false);
  };

  return (
    <div style={styles.container}>
      <Sidebar
        passwords={passwords}
        selected={selected}
        onSelect={handleSelect}
        onCreateNew={handleCreateNew}
      />
      <MainPanel
        selected={selected}
        isEditing={isEditing}
        showPassword={showPassword}
        onFieldChange={handleFieldChange}
        setIsEditing={setIsEditing}
        setShowPassword={setShowPassword}
        onSave={handleSave}
        onDelete={handleDelete}
      />
    </div>
  );
}

// Sidebar Component: Displays logo, vault items, and a "Create New" button.
function Sidebar({ passwords, selected, onSelect, onCreateNew }) {
  return (
    <div style={styles.sidebar}>
      <img src={logo} alt="Gandalf Logo" style={styles.logo} />
      <h2 style={styles.sidebarTitle}>My Vault</h2>
      <ul style={styles.list}>
        {passwords.map((entry, idx) => (
          <li
            key={idx}
            style={selected === entry ? styles.listItemActive : styles.listItem}
            onClick={() => onSelect(entry)}
          >
            {entry.site || "Untitled"}
          </li>
        ))}
      </ul>
      <button style={styles.newButton} onClick={onCreateNew}>
        <FaPlus style={{ marginRight: 5 }} />
        Create New
      </button>
    </div>
  );
}

// MainPanel Component: Shows the details and editing options for the selected entry.
function MainPanel({
  selected,
  isEditing,
  showPassword,
  onFieldChange,
  setIsEditing,
  setShowPassword,
  onSave,
  onDelete,
}) {
  if (!selected) {
    return (
      <div style={styles.main}>
        <h1 style={styles.title}>Manage Passwords</h1>
        <p>Please select a vault item or create a new one.</p>
      </div>
    );
  }

  return (
    <div style={styles.main}>
      <h1 style={styles.title}>Manage Passwords</h1>
      <div style={styles.details}>
        {/* Name */}
        <InfoRow label="Name">
          {isEditing ? (
            <input
              style={styles.input}
              type="text"
              value={selected.site}
              onChange={(e) => onFieldChange("site", e.target.value)}
            />
          ) : (
            selected.site || "Untitled"
          )}
        </InfoRow>

        {/* Username */}
        <InfoRow
          label="Username"
          icon={
            !isEditing && (
              <FaCopy
                style={styles.icon}
                title="Copy username"
                onClick={() => navigator.clipboard.writeText(selected.username)}
              />
            )
          }
        >
          {isEditing ? (
            <input
              style={styles.input}
              type="text"
              value={selected.username}
              onChange={(e) => onFieldChange("username", e.target.value)}
            />
          ) : (
            selected.username
          )}
        </InfoRow>

        {/* Password */}
        <InfoRow
          label="Password" //Password
          icon={
            !isEditing &&
            (showPassword ? (
              <FaEyeSlash
                style={styles.icon}
                title="Hide password"
                onClick={() => setShowPassword(false)}
              />
            ) : (
              <FaEye
                style={styles.icon}
                title="Show password"
                onClick={() => setShowPassword(true)}
              />
            ))
          }
        >
          {isEditing ? (
            <input
              style={styles.input}
              type="text"
              value={selected.password}
              onChange={(e) => onFieldChange("password", e.target.value)}
            />
          ) : showPassword ? (
            selected.password
          ) : (
            "••••••••"
          )}
        </InfoRow>

        {/* Website */}
        <InfoRow label="Website"> 
          {isEditing ? (
            <input
              style={styles.input}
              type="text"
              value={selected.website}
              onChange={(e) => onFieldChange("website", e.target.value)}
            />
          ) : selected.website ? (
            <a
              style={styles.link}
              href={`https://${selected.website}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {selected.website}
            </a>
          ) : null}
        </InfoRow>

        {/* Action Buttons */}
        <div style={{ marginTop: "10px" }}>
          {isEditing ? (
            <button style={styles.actionButton} onClick={onSave}>
              <FaPencilAlt style={{ marginRight: 5 }} />
              Save
            </button>
          ) : (
            <button
              style={styles.actionButton}
              onClick={() => setIsEditing(true)}
            >
              <FaPencilAlt style={{ marginRight: 5 }} />
              Edit
            </button>
          )}
          <button style={styles.deleteButton} onClick={onDelete}>
            <FaTrash style={{ marginRight: 5 }} />
            Delete
          </button>
        </div>
      </div>
    </div>
  );
}

function InfoRow({ label, children, icon }) {
  return (
    <div style={styles.infoRow}>
      <div style={styles.labelColumn}>
        <strong>{label}:</strong>
      </div>
      <div style={styles.valueColumn}>{children}</div>
      {icon && <div style={styles.iconColumn}>{icon}</div>}
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "#e0f7fa",
  },
  sidebar: {
    width: "250px",
    background: "#add8e6",
    padding: "20px",
    boxSizing: "border-box",
  },
  logo: {
    width: "80px",
    height: "80px",
    marginBottom: "10px",
    display: "block",
  },
  sidebarTitle: {
    fontSize: "20px",
    color: "#fff",
    marginBottom: "10px",
  },
  list: {
    listStyle: "none",
    padding: 0,
    marginBottom: "20px",
  },
  listItem: {
    padding: "8px",
    cursor: "pointer",
    borderBottom: "1px solid #fff",
    color: "#000",
  },
  listItemActive: {
    padding: "8px",
    cursor: "pointer",
    backgroundColor: "#007bff",
    color: "#fff",
    borderBottom: "1px solid #fff",
  },
  newButton: {
    width: "100%",
    padding: "8px 12px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "14px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  main: {
    flex: 1,
    padding: "20px",
    boxSizing: "border-box",
  },
  title: {
    fontSize: "26px",
    marginTop: 0,
    marginBottom: "20px",
  },
  details: {
    maxWidth: "600px",
  },
  infoRow: {
    display: "flex",
    alignItems: "center",
    marginBottom: "12px",
  },
  labelColumn: {
    width: "90px",
