import React, { useState } from "react";
import { FaPlus, FaCopy, FaEye, FaEyeSlash } from "react-icons/fa";
import logo from "../images/gandalflogo.jpg";

const initialPasswords = [
  {
    site: "Bestbuy",
    username: "Testingaccount",
    password: "password123",
    website: "bestbuy.com",
  },
  {
    site: "Amazon",
    username: "Testingaccount",
    password: "amazonpass",
    website: "amazon.com",
  },
  {
    site: "Netflix",
    username: "Testingaccount",
    password: "netflixpass",
    website: "netflix.com",
  },
];

export default function PasswordManager() {
  const [passwords, setPasswords] = useState(initialPasswords);
  const [selected, setSelected] = useState(passwords[0]);
  const [isEditing, setIsEditing] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleSave = () => {
    setPasswords(prev => 
      prev.map(entry => (entry === selected ? selected : entry))
    );
    setIsEditing(false);
  };

  const handleCreateNew = () => {
    const blank = { site: "", username: "", password: "", website: "" };
    setPasswords([...passwords, blank]);
    setSelected(blank);
    setIsEditing(true);
    setShowPassword(false);
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <img src={logo} alt="Gandalf Logo" style={styles.logo} />
        <h2 style={styles.sidebarTitle}>My Vault</h2>

        <ul style={styles.list}>
          {passwords.map((entry, idx) => (
            <li
              key={idx}
              style={selected === entry ? styles.listItemActive : styles.listItem}
              onClick={() => {
                setSelected(entry);
                setIsEditing(false);
                setShowPassword(false);
              }}
            >
              {entry.site || "Untitled"}
            </li>
          ))}
        </ul>

        <button style={styles.newButton} onClick={handleCreateNew}>
          <FaPlus style={{ marginRight: 5 }} />
          Create New
        </button>
      </div>

      {/* Main Panel */}
      <div style={styles.main}>
        <h1 style={styles.title}>Password Manager</h1>

        {selected && (
          <div style={styles.details}>
            {/* Name */}
            <InfoRow label="Name:">
              {isEditing ? (
                <input
                  type="text"
                  value={selected.site}
                  onChange={e => setSelected({ ...selected, site: e.target.value })}
                />
              ) : (
                selected.site || "Untitled"
              )}
            </InfoRow>

            {/* Username */}
            <InfoRow label="Username:">
              {isEditing ? (
                <input
                  type="text"
                  value={selected.username}
                  onChange={e => setSelected({ ...selected, username: e.target.value })}
                />
              ) : (
                selected.username
              )}
            </InfoRow>
            <div style={styles.iconColumn}>
              <FaCopy
                style={styles.icon}
                onClick={() => navigator.clipboard.writeText(selected.username)}
                title="Copy username"
              />
            </div>

            {/* Password */}
            <InfoRow label="Password:">
              {isEditing ? (
                <input
                  type="text"
                  value={selected.password}
                  onChange={e => setSelected({ ...selected, password: e.target.value })}
                />
              ) : showPassword ? (
                selected.password
              ) : (
                "••••••••"
              )}
            </InfoRow>
            <div style={styles.iconColumn}>
              {showPassword ? (
                <FaEyeSlash
                  style={styles.icon}
                  onClick={() => setShowPassword(false)}
                  title="Hide password"
                />
              ) : (
                <FaEye
                  style={styles.icon}
                  onClick={() => setShowPassword(true)}
                  title="Show password"
                />
              )}
            </div>

            {/* Website */}
            <InfoRow label="Website:">
              {isEditing ? (
                <input
                  type="text"
                  value={selected.website}
                  onChange={e => setSelected({ ...selected, website: e.target.value })}
                />
              ) : selected.website ? (
                <a
                  href={`https://${selected.website}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={styles.link}
                >
                  {selected.website}
                </a>
              ) : null}
            </InfoRow>

            {/* Edit or Save */}
            {isEditing ? (
              <button style={styles.actionButton} onClick={handleSave}>
                Save
              </button>
            ) : (
              <button style={styles.actionButton} onClick={() => setIsEditing(true)}>
                Edit
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

/* 
 * Simple reusable layout for label + value.
 * Renders a row with a bold label and a center-aligned value. 
 */
function InfoRow({ label, children }) {
  return (
    <div style={styles.infoRow}>
      <div style={styles.labelColumn}>
        <strong>{label}</strong>
      </div>
      <div style={styles.valueColumn}>{children}</div>
    </div>
  );
}

/* Styles */
const styles = {
  container: {
    display: "flex",
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "#e0f7fa",
    margin: 0,
    padding: 0,
    overflow: "hidden",
    boxSizing: "border-box",
  },
  sidebar: {
    width: "250px",
    background: "#add8e6",
    padding: "20px",
    boxSizing: "border-box",
    textAlign: "left",
  },
  logo: {
    width: "80px",
    height: "80px",
    marginBottom: "10px",
    display: "block",
  },
  sidebarTitle: {
    fontSize: "20px",
    color: "#fff",
    marginBottom: "10px",
  },
  list: {
    listStyle: "none",
    padding: 0,
    marginBottom: "20px",
  },
  listItem: {
    padding: "8px",
    cursor: "pointer",
    borderBottom: "1px solid #fff",
    color: "#000",
  },
  listItemActive: {
    padding: "8px",
    cursor: "pointer",
    backgroundColor: "#007bff",
    color: "#fff",
    borderBottom: "1px solid #fff",
  },
  newButton: {
    width: "100%",
    padding: "8px 12px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "14px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  main: {
    flex: 1,
    padding: "20px",
    boxSizing: "border-box",
  },
  title: {
    fontSize: "26px",
    marginTop: 0,
    marginBottom: "20px",
  },
  details: {
    maxWidth: "600px",
  },
  infoRow: {
    display: "flex",
    alignItems: "center",
    backgroundColor: "#fff",
    borderRadius: "5px",
    marginBottom: "10px",
    padding: "10px",
  },
  labelColumn: {
    flex: 1,
    textAlign: "left",
    fontWeight: "bold",
  },
  valueColumn: {
    flex: 1,
    textAlign: "center",
  },
  iconColumn: {
    flex: 1,
    textAlign: "right",
  },
  icon: {
    cursor: "pointer",
    fontSize: "1.2em",
    marginLeft: "10px",
  },
  link: {
    color: "#007bff",
    textDecoration: "none",
  },
  actionButton: {
    marginTop: "10px",
    padding: "8px 16px",
    backgroundColor: "#007bff",
    border: "none",
    color: "#fff",
    borderRadius: "4px",
    cursor: "pointer",
  },
};
