import React, { useState, useEffect } from "react";
import logo from "../images/gandalflogo.jpg";
import { FaPlus, FaCopy, FaEye, FaEyeSlash } from "react-icons/fa";

export default function PasswordManager() {
  const [passwords, setPasswords] = useState([]);
  const [selected, setSelected] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // 1) On mount, load from localStorage (if anything was saved)
  useEffect(() => {
    const stored = localStorage.getItem("passwords");
    if (stored) {
      setPasswords(JSON.parse(stored));
      // If you want to auto-select the first item (as in your original code):
      const parsed = JSON.parse(stored);
      if (parsed.length > 0) setSelected(parsed[0]);
    } else {
      // If there is nothing in localStorage, you can also initialize with
      // your sample data, if you like:
      const sampleData = [
        {
          site: "Bestbuy",
          username: "Testingaccount",
          password: "password123",
          website: "bestbuy.com",
        },
        {
          site: "Amazon",
          username: "Testingaccount",
          password: "amazonpass",
          website: "amazon.com",
        },
        {
          site: "Netflix",
          username: "Testingaccount",
          password: "netflixpass",
          website: "netflix.com",
        },
      ];
      setPasswords(sampleData);
      setSelected(sampleData[0]);
    }
  }, []);

  // 2) Whenever `passwords` changes, persist it to localStorage
  useEffect(() => {
    localStorage.setItem("passwords", JSON.stringify(passwords));
  }, [passwords]);

  const handleSave = () => {
    // Actually apply the edited selected item back into the array
    setPasswords((prev) => prev.map((p) => (p === selected ? selected : p)));
    setIsEditing(false);
  };

  const handleCreateNew = () => {
    const blank = {
      site: "",
      username: "",
      password: "",
      website: "",
    };
    setPasswords((prev) => [...prev, blank]);
    setSelected(blank);
    setIsEditing(true);
    setShowPassword(false);
  };

  return (
    <div style={styles.container}>
      {/* SIDEBAR */}
      <div style={styles.sidebar}>
        <img src={logo} alt="Gandalf Logo" style={styles.logo} />
        <h2 style={styles.sidebarTitle}>My Vault</h2>

        <ul style={styles.list}>
          {passwords.map((entry, i) => (
            <li
              key={i}
              onClick={() => {
                setSelected(entry);
                setIsEditing(false);
                setShowPassword(false);
              }}
              style={
                selected === entry ? styles.listItemActive : styles.listItem
              }
            >
              {entry.site || "Untitled"}
            </li>
          ))}
        </ul>

        <button style={styles.newButton} onClick={handleCreateNew}>
          <FaPlus style={{ marginRight: 5 }} />
          Create New
        </button>
      </div>

      {/* MAIN */}
      <div style={styles.main}>
        <h1 style={styles.title}>Password Manager</h1>

        {selected && (
          <div style={styles.details}>
            {/* NAME */}
            <div style={styles.infoRow}>
              <div style={styles.labelColumn}>
                <strong>Name:</strong>
              </div>
              <div style={styles.valueColumn}>
                {isEditing ? (
                  <input
                    type="text"
                    value={selected.site}
                    onChange={(e) =>
                      setSelected({ ...selected, site: e.target.value })
                    }
                  />
                ) : (
                  selected.site || "Untitled"
                )}
              </div>
              <div style={styles.iconColumn} />
            </div>

            {/* USERNAME */}
            <div style={styles.infoRow}>
              <div style={styles.labelColumn}>
                <strong>Username:</strong>
              </div>
              <div style={styles.valueColumn}>
                {isEditing ? (
                  <input
                    type="text"
                    value={selected.username}
                    onChange={(e) =>
                      setSelected({ ...selected, username: e.target.value })
                    }
                  />
                ) : (
                  selected.username
                )}
              </div>
              <div style={styles.iconColumn}>
                <FaCopy
                  style={styles.icon}
                  onClick={() =>
                    navigator.clipboard.writeText(selected.username)
                  }
                  title="Copy username"
                />
              </div>
            </div>

            {/* PASSWORD */}
            <div style={styles.infoRow}>
              <div style={styles.labelColumn}>
                <strong>Password:</strong>
              </div>
              <div style={styles.valueColumn}>
                {isEditing ? (
                  <input
                    type="text"
                    value={selected.password}
                    onChange={(e) =>
                      setSelected({ ...selected, password: e.target.value })
                    }
                  />
                ) : showPassword ? (
                  selected.password
                ) : (
                  "••••••••"
                )}
              </div>
              <div style={styles.iconColumn}>
                {showPassword ? (
                  <FaEyeSlash
                    style={styles.icon}
                    onClick={() => setShowPassword(false)}
                    title="Hide password"
                  />
                ) : (
                  <FaEye
                    style={styles.icon}
                    onClick={() => setShowPassword(true)}
                    title="Show password"
                  />
                )}
              </div>
            </div>

            {/* WEBSITE */}
            <div style={styles.infoRow}>
              <div style={styles.labelColumn}>
                <strong>Website:</strong>
              </div>
              <div style={styles.valueColumn}>
                {isEditing ? (
                  <input
                    type="text"
                    value={selected.website}
                    onChange={(e) =>
                      setSelected({ ...selected, website: e.target.value })
                    }
                  />
                ) : selected.website ? (
                  <a
                    href={`https://${selected.website}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={styles.link}
                  >
                    {selected.website}
                  </a>
                ) : (
                  ""
                )}
              </div>
              <div style={styles.iconColumn} />
            </div>

            {/* EDIT or SAVE BUTTON */}
            {isEditing ? (
              <button style={styles.actionButton} onClick={handleSave}>
                Save
              </button>
            ) : (
              <button
                style={styles.actionButton}
                onClick={() => setIsEditing(true)}
              >
                Edit
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

/* --- STYLES --- */
const styles = {
  /* The entire window has one background color */
  container: {
    display: "flex",
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "#e0f7fa",
    margin: 0,
    padding: 0,
    overflow: "hidden",
    boxSizing: "border-box",
  },

  /* SIDEBAR STYLES */
  sidebar: {
    width: "250px",
    background: "#add8e6",
    padding: "20px",
    boxSizing: "border-box",
    textAlign: "left",
  },
  logo: {
    width: "80px",
    height: "80px",
    marginBottom: "10px",
    display: "block",
  },
  sidebarTitle: {
    fontSize: "20px",
    color: "#fff",
    marginBottom: "10px",
  },
  list: {
    listStyle: "none",
    padding: 0,
    marginBottom: "20px",
  },
  listItem: {
    padding: "8px",
    cursor: "pointer",
    borderBottom: "1px solid #fff",
    color: "#000",
  },
  listItemActive: {
    padding: "8px",
    cursor: "pointer",
    backgroundColor: "#007bff",
    color: "#fff",
    borderBottom: "1px solid #fff",
  },
  newButton: {
    width: "100%",
    padding: "8px 12px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "14px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },

  /* MAIN PANEL STYLES */
  main: {
    flex: 1,
    padding: "20px",
    boxSizing: "border-box",
  },
  title: {
    fontSize: "26px",
    marginTop: 0,
    marginBottom: "20px",
  },
  details: {
    maxWidth: "600px",
  },

  // 3-column layout: label (left), value (center), icons (right)
  infoRow: {
    display: "flex",
    alignItems: "center",
    backgroundColor: "#fff",
    borderRadius: "5px",
    marginBottom: "10px",
    padding: "10px",
  },
  labelColumn: {
    flex: 1,
    textAlign: "left",
    fontWeight: "bold",
  },
  valueColumn: {
    flex: 1,
    textAlign: "center",
  },
  iconColumn: {
    flex: 1,
    textAlign: "right",
  },
  icon: {
    cursor: "pointer",
    fontSize: "1.2em",
    marginLeft: "10px",
  },
  link: {
    color: "#007bff",
    textDecoration: "none",
  },
  actionButton: {
    marginTop: "10px",
    padding: "8px 16px",
    backgroundColor: "#007bff",
    border: "none",
    color: "#fff",
    borderRadius: "4px",
    cursor: "pointer",
  },
};
