import React, { useState } from "react";
import logo from "../images/gandalflogo.jpg"; // Import logo image

const PasswordManager = () => {
  const [passwords, setPasswords] = useState([]);
  const [formData, setFormData] = useState({
    site: "",
    username: "",
    password: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setPasswords([...passwords, formData]);
    setFormData({ site: "", username: "", password: "" });
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <img src={logo} alt="Gandalf Logo" style={styles.logo} />
        <h1 style={styles.title}>Gandalf Password Manager</h1>
        <h2 style={styles.subtitle}>Manage Your Passwords</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <input
            type="text"
            name="site"
            placeholder="Website/App Name"
            value={formData.site}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <input
            type="text"
            name="username"
            placeholder="Username/Email"
            value={formData.username}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Save Password</button>
        </form>
        <h3 style={styles.listTitle}>Saved Passwords</h3>
        <ul style={styles.list}>
          {passwords.map((entry, index) => (
            <li key={index} style={styles.listItem}>
              <strong>{entry.site}</strong>: {entry.username} - {" ".repeat(entry.password.length)}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#e0f7fa",
  },
  logo: {
    width: "100px",
    height: "100px",
    marginBottom: "20px",
  },
  card: {
    background: "#add8e6",
    padding: "20px",
    borderRadius: "10px",
    textAlign: "center",
    width: "350px",
  },
  title: {
    fontFamily: "Virust, sans-serif",
    fontSize: "28px",
    color: "white",
  },
  subtitle: {
    fontFamily: "Virust, sans-serif",
    fontSize: "22px",
    color: "black",
    marginBottom: "15px",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  input: {
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  button: {
    padding: "10px",
    backgroundColor: "#333",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  listTitle: {
    marginTop: "20px",
    fontSize: "18px",
    color: "black",
  },
  list: {
    listStyle: "none",
    padding: 0,
  },
  listItem: {
    background: "#fff",
    padding: "8px",
    borderRadius: "5px",
    marginBottom: "5px",
    fontSize: "14px",
  },
};

export default PasswordManager;
